class ThemeManager{constructor(){this.themeToggle=document.getElementById('theme-toggle');this.themeIcon=document.getElementById('theme-icon');this.init();}init(){const savedTheme=localStorage.getItem('theme')|| 'dark';this.setTheme(savedTheme);this.themeToggle.addEventListener('click',()=>{this.toggleTheme();});window.matchMedia('(prefers-color-scheme: dark)').addEventListener('change',(e)=>{if(!localStorage.getItem('theme')){this.setTheme(e.matches ? 'dark' : 'light');}});}setTheme(theme){const html=document.documentElement;if(theme==='dark'){html.classList.add('dark');this.themeIcon.className='fas fa-sun text-gray-600 dark:text-accent-orange text-lg';}else{html.classList.remove('dark');this.themeIcon.className='fas fa-moon text-gray-600 dark:text-accent-orange text-lg';}localStorage.setItem('theme',theme);window.dispatchEvent(new CustomEvent('themeChanged',{detail:{theme}}));}toggleTheme(){const currentTheme=document.documentElement.classList.contains('dark')? 'dark' : 'light';const newTheme=currentTheme==='dark' ? 'light' : 'dark';document.body.classList.add('theme-transitioning');this.setTheme(newTheme);setTimeout(()=>{document.body.classList.remove('theme-transitioning');},300);}getCurrentTheme(){return document.documentElement.classList.contains('dark')? 'dark' : 'light';}}document.addEventListener('DOMContentLoaded',()=>{window.themeManager=new ThemeManager();});if(typeof module !=='undefined' && module.exports){module.exports=ThemeManager;}